import React, { useState } from "react";
import api from "../api";
import { useNavigate } from "react-router-dom";
import "./SignUp.css"; // 스타일 파일을 import

const SignUp = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [year, setYear] = useState("");
  const [month, setMonth] = useState("");
  const [day, setDay] = useState("");
  const [gender, setGender] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handlePasswordChange = (e) => setPassword(e.target.value);
  const handleConfirmPasswordChange = (e) => setConfirmPassword(e.target.value);
  const handleNameChange = (e) => setName(e.target.value);
  const handleEmailChange = (e) => setEmail(e.target.value);
  const handleYearChange = (e) => setYear(e.target.value);
  const handleMonthChange = (e) => setMonth(e.target.value);
  const handleDayChange = (e) => setDay(e.target.value);
  const handleGenderChange = (e) => setGender(e.target.value);
  const handlePhoneNumberChange = (e) => setPhoneNumber(e.target.value);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (password !== confirmPassword) {
      setError("Passwords do not match");
      return;
    }

    const registerRequest = {
      name: name,
      email: email,
      password: password,
      year: year,
      month: month,
      day: day,
      gender: gender,
      phoneNumber: phoneNumber,
    };

    try {
      // 회원가입 요청
      await api.post("/users/register", registerRequest);
      console.log("Registration successful");

      // 로그인 요청
      const loginRequest = {
        email: email,
        password: password,
      };

      const response = await api.post("/users/authenticate", loginRequest);
      const accessToken = response.data.body?.accessToken;
      const refreshToken = response.data.body?.refreshToken;
      if (accessToken && refreshToken) {
        localStorage.setItem("token", accessToken);
        localStorage.setItem("refreshToken", refreshToken);
        navigate("/submit-survey"); // 설문조사 페이지로 이동
      } else {
        setError("Token not found in response");
      }
    } catch (error) {
      console.error("Error during registration or login", error);
      setError("Registration or login failed");
    }
  };

  // 년도 옵션 생성
  const yearOptions = [];
  for (let year = 1960; year <= 2024; year++) {
    yearOptions.push(
      <option key={year} value={year}>
        {year}년
      </option>
    );
  }

  // 월 옵션 생성
  const monthOptions = [];
  for (let month = 1; month <= 12; month++) {
    monthOptions.push(
      <option key={month} value={month < 10 ? `0${month}` : `${month}`}>
        {month}월
      </option>
    );
  }

  // 일 옵션 생성
  const dayOptions = [];
  for (let day = 1; day <= 31; day++) {
    dayOptions.push(
      <option key={day} value={day < 10 ? `0${day}` : `${day}`}>
        {day}일
      </option>
    );
  }

  return (
    <div className="signup-container">
      <form onSubmit={handleSubmit} className="signup-form">
        <h1 className="signup-title">회원가입</h1>
        {error && <p className="error">{error}</p>}
        <div className="form-group">
          <label>이름</label>
          <input
            type="text"
            value={name}
            onChange={handleNameChange}
            required
            className="signup-input"
          />
        </div>
        <div className="form-group">
          <label>이메일</label>
          <input
            type="email"
            value={email}
            onChange={handleEmailChange}
            required
            className="signup-input"
          />
        </div>
        <div className="form-group">
          <label>비밀번호</label>
          <input
            type="password"
            value={password}
            onChange={handlePasswordChange}
            required
            className="signup-input"
          />
        </div>
        <div className="form-group">
          <label>비밀번호 확인</label>
          <input
            type="password"
            value={confirmPassword}
            onChange={handleConfirmPasswordChange}
            required
            className="signup-input"
          />
        </div>
        <div className="form-group">
          <label>생년월일</label>
          <div className="birthdate-inputs">
            <select value={year} onChange={handleYearChange} required>
              <option value="">연도</option>
              {yearOptions}
            </select>
            <select value={month} onChange={handleMonthChange} required>
              <option value="">월</option>
              {monthOptions}
            </select>
            <select value={day} onChange={handleDayChange} required>
              <option value="">일</option>
              {dayOptions}
            </select>
          </div>
        </div>
        <div className="form-group">
          <label>성별</label>
          <select value={gender} onChange={handleGenderChange} required>
            <option value="">선택</option>
            <option value="male">남성</option>
            <option value="female">여성</option>
            <option value="other">기타</option>
          </select>
        </div>
        <div className="form-group">
          <label>전화번호</label>
          <input
            type="tel"
            value={phoneNumber}
            onChange={handlePhoneNumberChange}
            required
            className="signup-input"
          />
        </div>
        <button type="submit" className="signup-button">회원가입</button>
      </form>
    </div>
  );
};

export default SignUp;
